export default Posts_CN = [
	{
	  type: "sections",
	  thumbnail: require("../assets/Test2.png"),
	  title: "了解中风",
	  //subtitle: "What is a stroke?",
	  body: [
		{
		  type: "details",
		  thumbnail: require("../assets/Test2.png"),
		  title: "关于中风",
		  subtitle: "什么是中风?",
		  body: [
			{
			  type: "img",
			  content: require("../assets/Test.png"),
			},
			{
			  type: "p",
			  content: "中风是一种影响大脑内血管的疾病，称为动脉。它对那些患有这种疾病的人可能是致命的，可能导致各种残疾甚至死亡。"
			},
			{
			  type: "h1",
			  content: "中风有多常见？"
			},
			{
			  type: "img",
			  content: require("../assets/OneinSix.png"),
			},
		  ]
		},
		{
		  type: "details",
		  thumbnail: require("../assets/Test3.png"),
		  title: "中风的类型",
		  subtitle: "两种中风是什么？",
		  body: [
			{
			  type: "h1",
			  content: "缺血性中风"
			},
			{
			  type: "img",
			  content: require("../assets/Test7.png"),
			},
			{
			  type: "p",
			  content: "-	当血液凝结在动脉中并切断向大脑的血液供应时发生。它通常是由栓塞或血栓形成引起的。 "
			},
			{
			  type: "img",
			  content: require("../assets/Blood.png"),
			},

			{
			  type: "h1",
			  content: "出血性中风"
			},
			{
			  type: "img",
			  content: require("../assets/Rupture.png"),
			},
			{
			  type: "p",
			  content: "当血管破裂导致脑部出血时发生。"
			},
			{
			  type: "p",
			  content: "通常由高血压或血液壁缺陷（脑动脉瘤）引起。"
			},
			{
			  type: "img",
			  content: require("../assets/cerebral2.png"),
			},
			{
				type: "h1",
				content: "短暂性脑缺血发作"
			},
			{
				type: "p",
				content: "当大脑的血液供应暂时中断时发生。"
			},
			{
				type: "p",
				content: "没有永久性脑损伤，症状在几分钟之内消失。"
			},
		  ]
		},
		{
		  type: "details",
		  thumbnail: require("../assets/Test4.png"),
		  title: "关于大脑",
		  subtitle: "中风可能会影响大脑的不同区域。",
		  body: [
			{
			  type: "h1",
			  content: "脑功能（左）"
			},
			{
			  type: "img",
			  content: require("../assets/LeftBrain.jpg"),
			},
			{
			  type: "p",
			  content: "-	大脑有左右两侧。每一侧都分为几个区域, 用于控制不同的功能。"
			},
			{
			  type: "h1",
			  content: "脑功能（右）"
			},
			{
			  type: "img",
			  content: require("../assets/Test6.jpg"),
			},
			{
			  type: "p",
			  content: "您的医生可能会描述您的中风位置。这是大脑的不同部分。"
			},
			{
				type: "img",
				content: require("../assets/Parts.png"),
			},
